import styled, { css } from 'styled-components';

const Pill = styled.div`
  position: relative;
  width: 10px;
  height: 10px;
  border-radius: 20px;
  background-color: #999;
  transition: all 0.4s;

  ${props => props.label && css`
    &:after {
      position: absolute;
      content: '${props.label}';
      width: max-content;
      color: ${props => props.active || props.complete ? (props.accentColor || '#24a19c'): '#999'};
      font-weight: ${props => props.active ? 600 : 400};
      text-transform: uppercase;
      font-size: 11px;
      bottom: -20px;
      left: 50%;
      transform: translateX(-50%);
    }
  `}

  ${props => props.active && css`
    width: 20px;
    height: 20px;
    background-color: ${props.accentColor || '#24a19c'};

    &:before {
      position: absolute;
      content: '';
      background-color: #fff;
      width: 8px;
      height: 8px;
      border-radius: 8px;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
    }
  `}

  ${props => props.complete && css`
    width: 20px;
    height: 20px;
    background-color: ${props.accentColor || '#24a19c'};

    &:before {
      position: absolute;
      content: 'âœ“';
      color: #fff;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
    }
  `}
`

export default Pill;

import styled, { css } from 'styled-components'

const Line = styled.div`
  height: 1.5px;
  background-color: #f2f0ed;
  flex: 1;
  transition: all 0.4s;

  ${ props => props.complete && css`
    background-color: ${props => props.accentColor || '#24a19c'};
    height: 2px;
  `}
`

export default Line

import React, { Children, Fragment, cloneElement } from 'react'
import Pill from './Pill'
import Line from './Line';

function renderHiddenElement(Component) {
  return <Component {...Component.props} style={{ ...Component.props.style, display: 'none'}}/>
}

export default function MultiStepForm(props) {
  return (
    <div style={styles.container}>
      <div style={styles.stepperContainer}>

      {React.Children.map(props.children, (child, i) => {
        if (i === Children.count(props.children) - 1)
        return (
          <Fragment>
              <Pill active={props.activeStep === (i+1)} label={child.props.label}
                complete={props.activeStep > i+1}
                accentColor={props.accentColor}/>
            </Fragment>
          )

          return (
            <Fragment>
            <Pill active={props.activeStep === (i+1)} complete={(props.activeStep > 1) && ((i+1) < props.activeStep)}
              label={child.props.label} accentColor={props.accentColor}/>
            <Line complete={(props.activeStep > 1) && ((i+1) < props.activeStep)}
              accentColor={props.accentColor}/>

          </Fragment>
        )
      })}
      </div>
      {
        React.Children.map(props.children, (child, i) => {
          if((i+1) === props.activeStep)
            return child
          return cloneElement(child, { hidden: true })
        })
      }
    </div>
  )
}

const styles = {
  stepperContainer: {
    display: 'flex',
    justifyContent: 'space-evenly',
    alignItems: 'center',
    marginBottom: 40
  }
}

import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: ${props => props.hidden ? 'none' : 'block'}
`

export default function Step(props) {
  return (
    <Container {...props}>
      {props.children}
    </Container>
  )
}
